   // TODO: display appropriate header
  const displayHeader = () => {
    const itemCount = products.length;
    return <strong>Products ({itemCount} item{itemCount !== 1 ? 's' : ''})</strong>;


  }


  return (
    <div id="product-table">
      <header>
      {displayHeader()}
      </header>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Department</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
        </tbody>
      </table>
    </div>
  )
}
