import React from 'react'

export const ProductTable = (props) => {

  const {products, columns}= props;

   // TODO: display appropriate header
  const displayHeader = () => {
    const itemCount = products.length;
    return <strong>Products ({itemCount} item{itemCount !== 1 ? 's' : ''})</strong>;
  }

   // TODO: display only chosen columns
  const displayColumns = () => {
    return (
      <thead>
        <tr>
          {Object.keys(columns).map((columnName, index) => {
            if (columns[columnName]) {
              return <th key={index}>{columnName}</th>;
            }
            return null;
          })}
        </tr>
      </thead>
    );
  }






  return (
    <div id="product-table">
      <header>
      {displayHeader()}
      </header>
      {displayColumns()}
      <table>
        {displayColumns()}
        <tbody>
          {products.map((product, index) => (
          <tr key={index}>
            {Object.keys(columns).map((columnName, index) => {
              if (columns[columnName]) {
                return <td key={index}>{product[columnName]}</td>;
              }
              return null;
            })}
          </tr>
        ))}
        </tbody>
      </table>
    </div>
  )
}
