import React, { useState, useEffect } from 'react'

import '../styles/Search.css';
import { ColumnToggle } from './ColumnToggle';
import { ProductTable } from './ProductTable';
import { ProductFilters } from './ProductFilters';

export const ProductSearch = (props) => {
  const [price, setPrice] = useState({ minPrice: '', maxPrice: '' });

  const [filteredItems, setFilteredItems] = useState(products);

  useEffect(() => {
    setFilteredItems(products);
  }, [products]);

  const [toggles, setToggles] = useState({
    id: true,
    name: true,
    department: true,
    price: true,
    currency: true,
  });

  const onPriceChange = (name, value) => {
    // TODO: implement price change handler
    setPrice(prevPrice => ({
      ...prevPrice,
      [name]: value === '' ? '' : parseFloat(value)
    }));
  }

  const onToggle = (name, checked) => {
    // TODO: implement column toggle handler
    setToggles(prevToggles => ({
      ...prevToggles,
      [name]: checked
    }));
  }


  const onFilter = () => {
    // TODO: implement handler for filtering products by price range
    const min = parseFloat(price.minPrice);
    const max = parseFloat(price.minPrice);
    console.log(min, max)
    if (!isNaN(min) && !isNaN(max)) {
      const filtered = props.products.filter(product =>
        product.price >= min && product.price <= max
      );
      setFilteredItems(filtered);
    } else {
      setFilteredItems(products); 
    }

  }

  useEffect(() => {
    applyFilters();
  }, [price, products]);


  // let filteredItems = [];
  return (
    <div className="Products">
      <ProductFilters
        minPrice={price.minPrice}
        maxPrice={price.maxPrice}
        onPriceChange={onPriceChange}
        onFilter={onFilter}
        />

      <ColumnToggle
        onToggle={onToggle}
        toggles={toggles} />

      <ProductTable
        products={filteredItems}
        columns={toggles} />
    </div>
  );
}

export default ProductSearch;
